mapper.update:
update e_book
set name = #{name,jdbcType=VARCHAR},
  price = #{price,jdbcType=REAL},
  category_id = #{categoryId,jdbcType=INTEGER},
  image = #{image,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, price, category_id, image
from e_book
where id = #{id,jdbcType=INTEGER}
